mapper.select:
SELECT * FROM d_notice 
WHERE 
n_Noticer=#{NNoticer} and n_Noticed=#{NNoticed};

SELECT * FROM d_notice


		${whereSql}



	ORDER BY ${orderBySql}


	ORDER BY n_Id DESC

mapper.update:
UPDATE d_notice
n_Noticer=#{NNoticer,jdbcType=VARCHAR},
n_Noticed=#{NNoticed,jdbcType=VARCHAR},
n_Notice_Time=#{NNoticeTime,jdbcType=VARCHAR},
n_Read_Status=#{NReadStatus,jdbcType=NUMERIC}
WHERE n_Id=#{NId}

mapper.delete:
DELETE FROM d_notice 
WHERE
n_Noticer=#{NNoticer} and n_Noticed=#{NNoticed};

mapper.select:
select dn.n_Noticer noticerId,dn.n_Notice_Time noticeTime,dn.n_Read_Status readStatus, dui.u_User_Name noticerName,dui.u_Head_Image headImg, (SELECT COUNT(1) FROM d_diary WHERE n_Writter=dui.u_User_Id AND FIND_IN_SET(n_Authority,'0,2')) diaryNum, (SELECT COUNT(DISTINCT LEFT(n_Time,10)) FROM d_diary WHERE n_Writter=dui.u_User_Id AND FIND_IN_SET(n_Authority,'0,2')) diaryDayNum, DATEDIFF(SYSDATE(),dui.u_Join_Time) joinDay, (select count(1) from d_notice where n_Noticed=dui.u_User_Id) fanNums
,(select count(1) from d_notice where n_Noticer=#{userId} and n_Noticed=dui.u_User_Id) ifAtten
from d_notice dn LEFT JOIN d_user_info dui ON dn.n_Noticer=dui.u_User_Id WHERE dn.n_Noticed=#{author} order by dn.n_Notice_Time desc
LIMIT #{start},#{perPage}
;

select dn.n_Noticed noticedId,dn.n_Notice_Time noticeTime,dn.n_Read_Status readStatus, dui.u_User_Name noticedName,dui.u_Head_Image headImg, (SELECT COUNT(1) FROM d_diary WHERE n_Writter=dui.u_User_Id AND FIND_IN_SET(n_Authority,'0,2')) diaryNum, (SELECT COUNT(DISTINCT LEFT(n_Time,10)) FROM d_diary WHERE n_Writter=dui.u_User_Id AND FIND_IN_SET(n_Authority,'0,2')) diaryDayNum, DATEDIFF(SYSDATE(),dui.u_Join_Time) joinDay, (select count(1) from d_notice where n_Noticed=dui.u_User_Id) fanNums
,(select count(1) from d_notice where n_Noticer=#{userId} and n_Noticed=dui.u_User_Id) ifAtten
from d_notice dn LEFT JOIN d_user_info dui ON dn.n_Noticed=dui.u_User_Id WHERE dn.n_Noticer=#{author} order by dn.n_Notice_Time desc
LIMIT #{start},#{perPage}
;

SELECT COUNT(1) FROM d_notice 
WHERE 1=1

	and n_Noticed=#{NNoticed} 


	and n_Noticer=#{NNoticer} 


	and find_in_set(n_Read_Status,#{NReadStatus})

;
