mapper.select:
SELECT * FROM d_diary


		${whereSql}



	ORDER BY ${orderBySql}


	ORDER BY n_Id DESC

select d.n_Id NId,d.n_Type NType,d.n_BookId NBookid,d.n_Writter NWritter, d.n_Title NTitle,d.n_Content NContent,d.n_Time NTime,d.n_Weather NWeather, d.n_Mood NMood,d.n_Location NLocation,d.n_AllowComment NAllowComment, d.n_Authority nAuthority,di.u_User_Name userName,di.u_Head_Image headImage, (SELECT count(1) FROM d_praise WHERE p_Diary=d.n_Id) praiseNum, (SELECT count(1) FROM d_store WHERE s_Diary=d.n_Id) storeNum from d_diary d left join d_user_info di on d.n_Writter=di.u_User_Id where 1=1
and n_Writter=#{author}
ORDER BY n_Time DESC,n_Id DESC
LIMIT ${page},10
;

select count(1) from d_diary where 1=1 

	 and n_Type =#{NType}


	 and n_Writter=#{NWritter}

ORDER BY n_Id desc;
